UPDATE email_templates 
SET 
  html_content = '<html><body style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px; background: linear-gradient(135deg, #f8fafc, #f1f8f6);"><div style="text-align: center; margin-bottom: 30px; padding: 20px; background: linear-gradient(135deg, hsl(338, 89%, 64%), hsl(338, 89%, 74%)); border-radius: 12px; color: white;"><h1 style="color: white; margin: 0; font-size: 28px;">Welcome to {{app_name}}!</h1></div><div style="background: white; padding: 30px; border-radius: 12px; margin-bottom: 20px; box-shadow: 0 4px 6px rgba(0,0,0,0.1);"><p style="font-size: 16px; line-height: 1.6; color: #374151;">Hi {{user_name}},</p><p style="font-size: 16px; line-height: 1.6; color: #374151;">Thank you for joining our CRM platform! We''re excited to help you build stronger relationships and grow your business.</p><p style="font-size: 16px; line-height: 1.6; color: #374151; font-weight: bold;">Here''s what you can do to get started:</p><ul style="color: #374151; font-size: 16px; line-height: 1.8;"><li>Complete your business profile</li><li>Import your existing contacts</li><li>Set up your follow-up cadences</li><li>Start tracking your networking activities</li></ul></div><div style="text-align: center; margin: 30px 0;"><a href="{{dashboard_url}}" style="background: linear-gradient(135deg, hsl(338, 89%, 64%), hsl(338, 89%, 74%)); color: white; padding: 15px 30px; text-decoration: none; border-radius: 8px; display: inline-block; font-weight: bold; font-size: 16px; box-shadow: 0 4px 6px rgba(0,0,0,0.1);">Go to Dashboard</a></div><p style="color: #64748b; font-size: 14px; text-align: center;">Need help? Reply to this email or visit our help center.</p></body></html>',
  variables = '{"app_name": "PinkWizard", "dashboard_url": "Dashboard URL", "user_name": "User Name"}'::jsonb,
  updated_at = now()
WHERE template_key = 'welcome_email'